INSERT INTO shoes(shoe_model, brand, colorway, price, description, year_released, image_1_url, image_2_url, image_3_url, image_4_url)
VALUES('Ultra Boost 4.0', 'Adidas', 'Game of Thrones House Targaryen', 180, 'Binge watch all seasons of Game of Thrones after copping the adidas Ultra Boost 4.0 Game of Thrones House Targaryen. These sneakers released in March 2019 and retailed for $180.', 2019, 'https://www.flightclub.com/media/catalog/product/cache/1/image/1600x1140/9df78eab33525d08d6e5fb8d27136e95/1/3/136000_01.jpg', 'https://www.flightclub.com/media/catalog/product/cache/1/image/1600x1140/9df78eab33525d08d6e5fb8d27136e95/1/3/136000_02.jpg', 'https://www.flightclub.com/media/catalog/product/cache/1/image/1600x1140/9df78eab33525d08d6e5fb8d27136e95/1/3/136000_03.jpg', 'https://www.flightclub.com/media/catalog/product/cache/1/image/1600x1140/9df78eab33525d08d6e5fb8d27136e95/1/3/136000_04.jpg');

INSERT INTO shoes(shoe_model, brand, colorway, price, description, year_released, image_1_url, image_2_url, image_3_url, image_4_url)
VALUES('Yeezy Boost 350 v2', 'Adidas', 'Clay', 220, 'If you''re searching for a new sneaker with major drip, then cop the adidas Yeezy Boost 350 V2 Clay. This Yeezy Boost 350 comes with a clay upper with orange accents, translucent stripe with orange accents, clay midsole, and clay sole. These sneakers released in March 2019 and retailed for $220. Add another swaggy 350 V2 to the rotation and buy them on StockX today.', 2019, 'https://stockx-360.imgix.net/Adidas-Yeezy-Boost-350-V2-Clay/Images/Adidas-Yeezy-Boost-350-V2-Clay/Lv2/img01.jpg?auto=format,compress&q=90&updated_at=1551987026&w=1300', 'https://stockx-360.imgix.net/Adidas-Yeezy-Boost-350-V2-Clay/Images/Adidas-Yeezy-Boost-350-V2-Clay/Lv2/img28.jpg?auto=format,compress&q=90&updated_at=1551987026&w=1300', 'https://stockx-360.imgix.net/Adidas-Yeezy-Boost-350-V2-Clay/Images/Adidas-Yeezy-Boost-350-V2-Clay/Lv2/img21.jpg?auto=format,compress&q=90&updated_at=1551987026&w=1300', 'https://stockx-360.imgix.net/Adidas-Yeezy-Boost-350-V2-Clay/Images/Adidas-Yeezy-Boost-350-V2-Clay/Lv2/img10.jpg?auto=format,compress&q=90&updated_at=1551987026&w=1300');

